import React, { useState } from "react";
import "./App.css";
import logo from "./janakalyan_logo.png";
import { file } from "./upload.png";

const UploadFile = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    setFile(event.dataTransfer.files[0]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div className="main-body">
      <div className="image-container"><img src={logo}></img></div>
      <div className="business-info">
        <div className="upload-container">
          <h3>Business Credit Automation</h3>
          <h2>Upload Excel Files</h2>
          <div
            className="drop-zone"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
          >
            <input type="file" id="fileInput" onChange={handleFileChange} hidden />
            <label htmlFor="fileInput" className="drop-label">
              <img src={file}></img>
              <p>Send your files here</p>
              <small>Click or Drag and Drop</small>
            </label>
          </div>
          {file && <p className="file-name">Selected: {file.name}</p>}
          <button className="upload-btn">Upload File</button>
        </div>
        </div>
    </div>
  );
};

export default UploadFile;
